var shuffle = require('hersshuffler');

var Card = function(val){
  this.rank = (val / 136)*34 + (val % 34) + 1;
  return this;
}

function Deck(length){
  length = length || 144;
  var sh = shuffle(length);
  var ret = [];
  for (var i=0; i<sh.length; i++){
      ret.push(new Card(sh[i]));
  }
  return ret;
}

var Deck = Deck;

exports.Card = Card;
exports.Deck = Deck;
